mapper.insert:
INSERT INTO purchase_transactions ( "purchaseId" , "timestamp" , "height" , "hash" , "contents" ) VALUES
( #{item.purchaseId} , ${item.timestamp} , ${item.height} , #{item.hash} , #{item.contents} )
ON CONFLICT ("purchaseId") DO NOTHING;

mapper.select:
SELECT "contents" FROM purchase_transactions
WHERE "timestamp" < ${timestamp}
    AND "canceled" = 'N'
    AND "stored" = 'N'
LIMIT 256;
