mapper.sql:
 CREATE TABLE IF NOT EXISTS dms.exchange_rates
 (
     "symbol"            VARCHAR(12) NOT NULL,
     "rate"              bigint NOT NULL,
     PRIMARY KEY ("symbol")
);

CREATE TABLE IF NOT EXISTS dms.dms_blocks
(
    "height"            bigint NOT NULL,
    "contents"          TEXT NOT NULL,
    PRIMARY KEY ("height")
);

mapper.select:
DROP TABLE dms.purchase_blocks;
DROP TABLE dms.purchase_transactions;
DROP TABLE dms.exchange_rates;
DROP TABLE dms.dms_blocks;
DROP TABLE dms.dms_signatures;

TRUNCATE TABLE dms.purchase_blocks;
TRUNCATE TABLE dms.purchase_transactions;
TRUNCATE TABLE dms.exchange_rates;
TRUNCATE TABLE dms.dms_blocks;
TRUNCATE TABLE dms.dms_signatures;
