mapper.sql:
CREATE TABLE IF NOT EXISTS purchases ( "purchaseId" VARCHAR(66) NOT NULL, "timestamp" BIGINT NOT NULL, "waiting" BIGINT NOT NULL, "account" VARCHAR(42) NOT NULL, "loyaltyType" INTEGER NOT NULL, "currency" VARCHAR(12) NOT NULL, "providePoint" BIGINT NOT NULL, "provideToken" BIGINT NOT NULL, "provideValue" BIGINT NOT NULL, "shopId" VARCHAR(66) NOT NULL, "shopCurrency" VARCHAR(12) NOT NULL, "shopProvidedAmount" BIGINT NOT NULL, "canceled" VARCHAR(1) DEFAULT 'N', "done" VARCHAR(1) DEFAULT 'N', PRIMARY KEY ("purchaseId") );
CREATE INDEX IF NOT EXISTS "purchases_account_index" on purchases ("account");
CREATE INDEX IF NOT EXISTS "purchases_shopId_index" on purchases ("shopId");

CREATE TABLE IF NOT EXISTS temporary_accounts
(
    "sequence"              BIGINT generated always as identity,
    "account"               VARCHAR(42)  NOT NULL,
    "temporary_account"     VARCHAR(42)  NOT NULL,
    "timestamp"             TIMESTAMP,
    PRIMARY KEY ("sequence")
);
CREATE SEQUENCE IF NOT EXISTS temporary_accounts_sequence_seq;
CREATE INDEX IF NOT EXISTS temporary_accounts_temporary_account_index
    on temporary_accounts (temporary_account);

mapper.select:
DROP TABLE payments;
DROP TABLE tasks;
DROP TABLE mobiles;
DROP TABLE purchases;
DROP TABLE delegators;
DROP TABLE temporary_accounts;
