mapper.insert:
INSERT INTO purchase_transactions ( "purchaseId" , "timestamp" , "provide_timestamp" , "height" , "hash" , "contents" ) VALUES
( #{item.purchaseId} , #{item.timestamp} , #{item.provide_timestamp} , #{item.height} , #{item.hash} , #{item.contents} )
ON CONFLICT ("purchaseId") DO NOTHING;

mapper.select:
SELECT "contents" FROM purchase_transactions
WHERE "provide_timestamp" <= #{timestamp}
    AND "canceled" = 'N'
    AND ("step" = 0 OR "step" = 4)
LIMIT 256;
